#!/usr/bin/python

import matplotlib.pyplot as plt
import numpy as np


def parse_file_as_list(path):
	f = open(path, "r")
	lines = f.read(); 
	mylist = [int(x) for x in lines.split(',') if x.strip().isdigit()]

	
	
	return mylist
		



def transform(x, k, d):
	return int((k*x + d)) 


results0 = parse_file_as_list("data/thresholded.txt")
results1 = parse_file_as_list("data/original.txt")

print(results0)
print(results1)

plt.plot(results0, color='b')
plt.plot(results1, color='r')
plt.show()


